DROP DATABASE IF EXISTS novaloja;
CREATE DATABASE novaloja;

USE novaloja;

CREATE TABLE clientes_loja (
  id INT PRIMARY KEY AUTO_INCREMENT,
  nome VARCHAR(100),
  email VARCHAR(100),
  telefone VARCHAR(20),
  cidade VARCHAR(50),
  idade INT,
  renda_mensal DECIMAL(10,2),
  status VARCHAR(20),
  data_cadastro DATE
);

